import React, { useState } from 'react';
import './styles/index.css';
import Map from './components/Map';
import LayerToggle from './components/LayerToggle';
import FilterControls from './components/FilterControls';

function App() {
  // Available map layers
  const mapLayers = [
    { id: 'clusters-circles', name: 'Clusters' },
    { id: 'cluster-counts', name: 'Cluster Counts' },
    { id: 'unclustered-points', name: 'All Points' },
    { id: 'graduate-points', name: 'Graduates' },
    { id: 'featured-points', name: 'Featured Learners' },
    { id: 'entrepreneur-points', name: 'Entrepreneurs' }
  ];

  // State for active layers and filters
  const [activeLayers, setActiveLayers] = useState([
    'clusters-circles', 
    'cluster-counts', 
    'unclustered-points'
  ]);

  const [filters, setFilters] = useState({});
  const [controlsOpen, setControlsOpen] = useState(true);
  const [isLoading, setIsLoading] = useState(false);

  // Toggle a layer on/off
  const handleToggleLayer = (layerId) => {
    setActiveLayers(prevLayers => {
      if (prevLayers.includes(layerId)) {
        return prevLayers.filter(id => id !== layerId);
      } else {
        return [...prevLayers, layerId];
      }
    });
  };

  // Apply filters
  const handleApplyFilters = (newFilters) => {
    setIsLoading(true);
    setFilters(newFilters);

    // Loading state will be managed by the Map component
    setTimeout(() => setIsLoading(false), 500);
  };

  // Toggle controls panel
  const toggleControls = () => {
    setControlsOpen(prev => !prev);
  };

  return (
    <div className="app-container">
      <header className="header">
        <h1>Supercluster Map</h1>
        <button 
          className="toggle-controls-btn"
          onClick={toggleControls}
        >
          {controlsOpen ? 'Hide Controls' : 'Show Controls'}
        </button>
      </header>

      <div className="map-container">
        <Map 
          activeLayers={activeLayers} 
          filters={filters}
        />

        {controlsOpen && (
          <div className="map-controls">
            <LayerToggle 
              layers={mapLayers} 
              activeLayers={activeLayers} 
              onToggleLayer={handleToggleLayer} 
            />

            <FilterControls 
              onApplyFilters={handleApplyFilters}
              isLoading={isLoading}
            />
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
