import React, { useState, useEffect } from 'react';
import superclusterAPI from '../api/superclusterAPI';

const FilterControls = ({ onApplyFilters, isLoading }) => {
  const [activeFilters, setActiveFilters] = useState({
    gender_female: false,
    is_graduate_learner: false,
    is_wage_employed: false,
    is_running_a_venture: false
  });

  const applyFilters = () => {
    // Build filter object based on active filters
    const filterObject = {};

    if (activeFilters.gender_female) {
      filterObject.gender = 'Female';
    }

    if (activeFilters.is_graduate_learner) {
      filterObject.is_graduate_learner = true;
    }

    if (activeFilters.is_wage_employed) {
      filterObject.is_wage_employed = true;
    }

    if (activeFilters.is_running_a_venture) {
      filterObject.is_running_a_venture = true;
    }

    onApplyFilters(filterObject);
  };

  const toggleFilter = (filterName) => {
    setActiveFilters(prev => {
      const updated = {
        ...prev,
        [filterName]: !prev[filterName]
      };
      return updated;
    });
  };

  // Apply filters whenever active filters change
  useEffect(() => {
    applyFilters();
  }, [activeFilters]);

  const handleReset = () => {
    setActiveFilters({
      gender_female: false,
      is_graduate_learner: false,
      is_wage_employed: false,
      is_running_a_venture: false
    });
  };

  return (
    <div className="filter-controls">
      <h3>Data Filters</h3>
      <div className="filter-buttons">
        <button 
          type="button" 
          className={`filter-btn ${activeFilters.gender_female ? 'active' : ''}`}
          onClick={() => toggleFilter('gender_female')}
          disabled={isLoading}
        >
          Female Only
        </button>

        <button 
          type="button" 
          className={`filter-btn ${activeFilters.is_graduate_learner ? 'active' : ''}`}
          onClick={() => toggleFilter('is_graduate_learner')}
          disabled={isLoading}
        >
          Graduates
        </button>

        <button 
          type="button" 
          className={`filter-btn ${activeFilters.is_wage_employed ? 'active' : ''}`}
          onClick={() => toggleFilter('is_wage_employed')}
          disabled={isLoading}
        >
          Employed
        </button>

        <button 
          type="button" 
          className={`filter-btn ${activeFilters.is_running_a_venture ? 'active' : ''}`}
          onClick={() => toggleFilter('is_running_a_venture')}
          disabled={isLoading}
        >
          Entrepreneurs
        </button>

        <button 
          type="button" 
          onClick={handleReset} 
          disabled={isLoading}
          className="reset-btn"
        >
          Reset Filters
        </button>
      </div>
    </div>
  );
};

export default FilterControls;
