import React, { useState, useEffect } from 'react';
import superclusterAPI from '../api/superclusterAPI';

const FilterControls = ({ onApplyFilters, isLoading }) => {
  const [availableFilters, setAvailableFilters] = useState({
    genders: [],
    filters: {}
  });

  const [filters, setFilters] = useState({
    gender: '',
    country_of_residence: '',
    is_graduate_learner: null,
    is_wage_employed: null,
    is_running_a_venture: null,
    is_featured: null,
    is_featured_video: null
  });

  useEffect(() => {
    const fetchAvailableFilters = async () => {
      try {
        const data = await superclusterAPI.getAvailableFilters();
        setAvailableFilters(data);
      } catch (error) {
        console.error('Error fetching available filters:', error);
      }
    };

    fetchAvailableFilters();
  }, []);

  const handleFilterChange = (e) => {
    const { name, value, type } = e.target;

    if (type === 'checkbox') {
      // Handle checkbox toggling between true, false, and null (no filter)
      const checked = e.target.checked;
      // If checked is true, set to true, otherwise set to null (no filter)
      setFilters(prev => ({
        ...prev,
        [name]: checked ? true : null
      }));
    } else {
      setFilters(prev => ({
        ...prev,
        [name]: value === '' ? null : value
      }));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Remove null values before sending
    const cleanedFilters = Object.fromEntries(
      Object.entries(filters).filter(([_, v]) => v !== null)
    );

    onApplyFilters(cleanedFilters);
  };

  const handleReset = () => {
    setFilters({
      gender: '',
      country_of_residence: '',
      is_graduate_learner: null,
      is_wage_employed: null,
      is_running_a_venture: null,
      is_featured: null,
      is_featured_video: null
    });

    onApplyFilters({});
  };

  return (
    <div className="filter-controls">
      <h3>Data Filters</h3>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="gender">Gender</label>
          <select 
            id="gender" 
            name="gender" 
            value={filters.gender || ''} 
            onChange={handleFilterChange}
          >
            <option value="">All</option>
            {availableFilters.genders?.map(gender => (
              <option key={gender} value={gender}>{gender}</option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="country">Country</label>
          <input
            type="text"
            id="country"
            name="country_of_residence"
            value={filters.country_of_residence || ''}
            onChange={handleFilterChange}
            placeholder="Enter country"
          />
        </div>

        <div className="form-group checkbox">
          <input
            type="checkbox"
            id="is_graduate_learner"
            name="is_graduate_learner"
            checked={filters.is_graduate_learner === true}
            onChange={handleFilterChange}
          />
          <label htmlFor="is_graduate_learner">Graduates Only</label>
        </div>

        <div className="form-group checkbox">
          <input
            type="checkbox"
            id="is_wage_employed"
            name="is_wage_employed"
            checked={filters.is_wage_employed === true}
            onChange={handleFilterChange}
          />
          <label htmlFor="is_wage_employed">Employed Only</label>
        </div>

        <div className="form-group checkbox">
          <input
            type="checkbox"
            id="is_running_a_venture"
            name="is_running_a_venture"
            checked={filters.is_running_a_venture === true}
            onChange={handleFilterChange}
          />
          <label htmlFor="is_running_a_venture">Entrepreneurs Only</label>
        </div>

        <div className="form-group checkbox">
          <input
            type="checkbox"
            id="is_featured"
            name="is_featured"
            checked={filters.is_featured === true}
            onChange={handleFilterChange}
          />
          <label htmlFor="is_featured">Featured Only</label>
        </div>

        <div className="form-group checkbox">
          <input
            type="checkbox"
            id="is_featured_video"
            name="is_featured_video"
            checked={filters.is_featured_video === true}
            onChange={handleFilterChange}
          />
          <label htmlFor="is_featured_video">With Featured Video Only</label>
        </div>

        <div className="filter-buttons">
          <button type="submit" disabled={isLoading}>
            Apply Filters
          </button>
          <button type="button" onClick={handleReset} disabled={isLoading}>
            Reset
          </button>
        </div>
      </form>
    </div>
  );
};

export default FilterControls;
